import React from "react";
import { connect } from 'react-redux';
function ViewDetails(props) {
  console.log("In view details",props.product)
    if(props.product===undefined){
return <div>Please select an item from Home tab to view details or <a href='/about'>Click here to redirect</a></div>

}else{
return <div><div class="card text-center">
<div class="card-header">
  Featured
</div>
<div class="card-body">
  <h5 class="card-title">Special title treatment</h5>
  <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
  <a href="#" class="btn btn-primary">Go somewhere</a>
</div>
<div class="card-footer text-muted">
  2 days ago
</div>
</div>{props.product.id}</div>
}


}
const mapStateToProps = state =>{
    return {
      products: state.products,
      product:state.product
    }
  }

  export default connect(mapStateToProps)(ViewDetails)
