import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Navbar from "./components/Navbar";
import About from "./components/About";
import Signout from "./components/Signout";
import Products from "./components/Products";
import { Route, BrowserRouter as Router, NavLink } from "react-router-dom";
import { MainItems,SubItems,SignStatus } from "./data/NavbarItems";
import EditProfile from "./components/EditProfile";

function App() {



  const handlerClick=(e)=>{
    //check if signed in 
    const check3 = (e.target.value==='Sign Out')?SignStatus.class="w3-dropdown-hover w3-hide-small":console.log('ok');
    //check if not signed in
    const check= (e.target.name==='About')?console.log("ok"):alert("Sign in");
    const check2 = (e.target.value==='Sign In')?alert("Sign in"):console.log('ok');
  }
  return (
    <Router>
    <div>
    <div class="w3-bar w3-blue w3-card">
      <nav style={{ margin: 10 }}>
      {MainItems.map((item) => {
          return (
            <NavLink
          to={item.url}
          className={item.class}
          name={item.name}
          onClick={handlerClick}
        >
          {item.name}
        </NavLink>

          );
        })}


      </nav>
      <div class="w3-dropdown-hover w3-hide-small">
          <button class={SignStatus.class}
          onClick={handlerClick}  title="More">
            {SignStatus.name}<i class="fa fa-caret-down"></i>
          </button>
          <div class="w3-dropdown-content w3-bar-block w3-card-4">
            {SubItems.map((item) => {
              return (
                <NavLink
                to={item.url}
                className={item.class}
                name={item.name}
                onClick={handlerClick}
              >
                {item.name}
              </NavLink>

              );
            })}
          </div>
        </div>
      </div>

      <Route path="/about"  exact component={About} />
      <Route path="/products" component={Products} />
      <Route path="/editprofile"  component={EditProfile} />
      <Route path="/signout" component={Signout} />
    </div>

  </Router>
  );
}

export default App;
