import React, { Component,useState,useEffect } from "react";
import { setProduct } from "../Redux/Actions/ActionCreators";
import {connect} from 'react-redux'
import { useHistory } from "react-router-dom";
import './DeleteItem.css'

class DeleteItem2 extends Component {
    constructor(props){

    }
    state = {

        checked:props.selectAll
       }
   handler=(e)=>{
        props.setProduct(props.data)
        let history = useHistory();
         switch(e.target.id){
           case 'view':  history.push('/products/view')
                        break;
           case 'edit':  history.push('/products/update')
                          break;
           case 'delete':  history.push('/products/delete')
                            break;

         }}


     onChange=(e)=>{
        if(e.target.checked){
this.setState({checked:false})
        }else{
this.setState({checked:true})     
        }
      }
    render() {
        return (<div> 

            <div class="card w-50">
            <label class="container"><input type="checkbox" onChange={onChange}checked={this.state.checked}/>
          <span class="checkmark"></span></label>

          <div class="card-body">

            <h5 class="card-title"> {props.data.productName}</h5>

        <p >Price :${props.data.price}</p>
        <p class="card-text">Description: {props.data.productDescription}</p>
        <p class="card-text">Manufacturer:{props.data.manufacturer}</p>
            <button id="delete"  class="btn btn-danger ">delete</button>
            <button id="view" onClick={handler}class="btn btn-primary">view</button>

          </div>
        </div>

          </div>  );
    }
}

       // 



const mapStateToProps = state =>{
  return {
    products:state.products,
    product: state.product
  }
}
const mapDispatchToProps = dispatch =>{
  return {
    setProduct:prod=>dispatch(setProduct(prod))
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(DeleteItem2);
