import React, { useEffect, useState } from "react";
import { useHistory,Redirect, BrowserRouter as Router } from "react-router-dom";
import store from "../Redux/Store";
import { connect } from 'react-redux';
import {addProduct, getProduct} from "../Redux/Actions/ActionCreators"
import ProductList from "./ProductList";
export  function About(props) {
  let history = useHistory();
  function handler(){

    history.push('/products/view')
  }

   const [products,setproducts]=useState(props.products)
   let [filteredProducts,filter]=useState([])
   const [search,setSearch]=useState([''])
   const handleClick = () => {

    if (search !== "") {
     filteredProducts =products.filter((product) => {
        return (
          product.productName
            .toLowerCase()
            .indexOf(search.toLowerCase().trim()) !== -1
        );
      });
    }
    filter(filteredProducts);
    console.log(filteredProducts);
  }

  const onChange = (event) => {
    setSearch( event.target.value);
    console.log("Change recorded");
    console.log(event.target.value);
    if (event.target.value === "") {
      console.log("resetting the books");
   // this.setState({ filteredBooks: this.state.books });
    }
  };
  useEffect(()=>{

    setproducts(props.products)
  })

  return (<div>Hey About!
      <div class="search-container">
        <div>
          {" "}
          <input
            id="inn"
            onChange={onChange}
            type="text"
            placeholder="Search.."
          ></input>
          <button class="btn btn-success " onClick={handleClick}>
            Search
          </button>
        </div></div>

    <ProductList list ={products}></ProductList>
    {/* <button onClick={handler}>test redux</button> */}

  </div>);



}
const mapStateToProps = state =>{
  return {
    products: state.products
  }
}
const mapDispatchToProps = dispatch =>{
  return {
    getProduct:()=>dispatch(getProduct)
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(About)
