import {GET_PRODUCT,ADD_PRODUCT,INITIALIZE_PRODUCT} from '../Actions/Actions'
import store from '../Store';

const initialState={
products:[]
}
 const crudReducer =(state=initialState,actionCreator)=>{
    switch(actionCreator.type){
        case INITIALIZE_PRODUCT: return{
            products:actionCreator.products
        }
        case GET_PRODUCT: return{
            ...state,
            products:state.products
        }
        // case ADD_PRODUCT :return {
        //  ...state,
        //  products: state.products
        // }
        default: return state
    }
}
export default crudReducer;
