import React, { Component } from 'react';
import {Bar, Line, Pie} from 'react-chartjs-2';

class Chart extends Component{
  constructor(props){
    super(props);
    this.state = {
      chartData:props.data

      }
    }  

  render(){
    return (
      <div className="chart">

        <Bar
          data={this.props.data}
          options={{
            title:{
              display:true,
              text:'TOP HITS',
              fontSize:20
            },
            legend:{
              display:true,
              position: 'right'
            },
          }}
        />

        <Line
          data={this.state.chartData}
          options={{
            title:{
              display:true,
              text:'TOP HITS',
              fontSize:20
            },
            legend:{
              display:true,
              position: 'right'
            },
          }}
        />

        <Pie
          data={this.state.chartData}
          options={{
            title:{
              display:true,
              text:'Largest Cities in Massachusetts',
              fontSize:20
            },
            legend:{
              display:true,
              position: 'right'
            },
          }}
        />

      </div>
    )
  }
}

export default Chart;
