import React, { useState } from "react";
import { Route, useHistory } from "react-router-dom";
import  './Register.css';
  import { useFormik, yupToFormErrors } from 'formik';
  import * as Yup from 'yup';
  import {connect} from 'react-redux'
  import axios from 'axios';
import { setUserSession, validateUser } from "../utils/mainUtils";

  const Register = (props) => {
      const history = useHistory();
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const validationSchema = Yup.object(
     {
       name: Yup.string().required(
            '*Name is required'
        ),
         username: Yup.string().required(
             '*Username is required'
         ),
        password: Yup.string().required(
          '*Password is required'
         ),
         password1: Yup.string().required(
            '* Re-enter Password'
           ),
           officeId: Yup.string().required(
            '* Office Id required'
           ),
     })

    const formik = useFormik({
      initialValues:{
          name:'',
        username:'',
        password:'',
        password1:'',
        officeId:''

      },
      onSubmit: values => {
        console.log(values)
    if  (values.password!=values.password1)
{   
    alert('passwords dont match')

}else{

        setError(null);
        setLoading(true);
        validateUser(values.username)?
        axios.post('http://localhost:3002/users',values).then(response => {
          setLoading(false);
          alert("Registration successful")

       //   props.history.push('/dashboard');
        }).catch(error => {
          setLoading(false);
          console.log(error);
          if (error.response.status === 401) setError(error.response.data.message);
          else setError("Something went wrong. Please try again later.");
        }):alert("Username already exists")
      // let x=JSON.stringify(values, null, 2);
     //  ProductActions.addProduct(x)
      }},
      validationSchema

    });

    return (
      <div  className='main4'>

     <form className='form-group4'onSubmit={formik.handleSubmit}>
     <h4 style={{alignSelf:'center'}} >  Register </h4><br></br>
     <label htmlFor="name">Enter Name </label>
       <input
        className='form-control pro'
          id="name"
          name="name"
          type="text"


          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.name}
        /> 
      {formik.errors.name&&formik.touched.name?<div><h7 style={{color: "red"}}>{formik.errors.name}</h7></div>:null}<br></br>
      <label htmlFor="username">Enter Username</label>
        <input
         className='form-control pro textcolorx'
          id="username"
          name="username"
          type="text"

          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.username}
        /> 
      {formik.errors.username&&formik.touched.username?<div><h7 style={{color: "red"}}>{formik.errors.username}</h7></div>:null}<br></br>


      <label htmlFor="password">Enter Password</label>
        <input
       className='form-control pro textcolorx'
          id="password"
          name="password"
          type="password" 
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.password}
        /> 
      {formik.errors.password&&formik.touched.password?<div><h7 style={{color: "red"}}>{formik.errors.password}</h7></div>:null}<br></br>
      <label htmlFor="password1">Re-enter Password</label>
        <input
        className='form-control pro textcolorx'
          id="password1"
          name="password1"
          type="password1"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.password1}
        /> 
      {formik.errors.password1&&formik.touched.password1?<div><h7 style={{color: "red"}}>{formik.errors.password1}</h7></div>:null}<br></br>
      <label htmlFor="officeId">Enter Office ID</label>
        <input
        className='form-control pro textcolorx'
          id="officeId"
          name="officeId"
          type="text"

          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.officeId}
        /> 
      {formik.errors.officeId&&formik.touched.officeId?<div><h7 style={{color: "red"}}>{formik.errors.officeId}</h7></div>:null}<br></br>
        <button type="submit" className='btn btn-lg cbac text-white'>Register</button>
        <a href='/signIn'>Back to Login</a>
      </form>

      </div>
    );


  };
  const mapDispatchToProps = dispatch =>{
      return {

      }
    }

    export default connect(null,mapDispatchToProps)(Register)
