import React, { useEffect, useState, lazy, Suspense } from "react";
import {
  useHistory,
  Redirect,
  BrowserRouter as Router,
} from "react-router-dom";
import "./About.css";
import { connect } from "react-redux";
import { CSSTransition } from "react-transition-group";
const ProductList = lazy(() => import("./ProductList"));
export function About(props) {
  let history = useHistory();
  function handler() {
    history.push("/products/view");
  }

  const [products, setProducts] = useState(props.products);
  const [search, setSearch] = useState("");
  const handleClick = () => {
    let filteredProducts = products.filter((product) => {
      return (
        product.productName
          .toLowerCase()
          .indexOf(search.toLowerCase().trim()) !== -1
      );
    });
    setProducts(filteredProducts);
  };

  const onChange = (event) => {
    setSearch(event.target.value);
    console.log("Change recorded");
    console.log(event.target.value);
    handleClick();
  };
  useEffect(() => {
    if (search === "") {
      console.log("in here");
      setProducts(props.products);
    }
  });

  return (
    <div class="ggg">
      <div class="search-container ggg  ">
        <div>
          {" "}
          <input
            class="input-text"
            id="inn"
            onChange={onChange}
            type="text"
            placeholder="Search any item..."
          ></input>
          <button class="btn btn-dark text-white"style={{position:"fixed"}} onClick={handleClick}>
            Search
          </button>
        </div>
      </div>
  <div class="mx-auto" style={{width:50}}><button>Features</button></div>
  <CSSTransition
       in={toggle}
      timeout={3500}
       classNames='balloon'
       unmountOnExit
     >  <div class="mx-auto" style={{width: 400,height:100,background:'red'}}>
      </div> 
      </CSSTransition>
      <Suspense fallback={<div>Loading</div>}>
        <ProductList list={products}></ProductList>
      </Suspense>

    </div>
  );
}
const mapStateToProps = (state) => {
  return {
    products: state.products,
  };
};


export default connect(mapStateToProps)(About);
