import {VIEW_PRODUCT,ADD_PRODUCT} from '../Actions/Actions'
import store from '../Store';

const initialState={
numofProducts:10
}
 const crudReducer =(state,actionCreator)=>{
    switch(actionCreator.type){
        case ADD_PRODUCT :return {
         ...state,
         numofProducts: state.numofProducts
        }
        default: return state
    }
}
export default crudReducer;
