import DeleteItem from "./DeleteItem";
import ProductItem from "./ProductItem";
export default function DeleteList(props) {

   const [selectAll,setSelectAll]= useState(false)



   useEffect(()=>{
    const selectItems=(value,data)=>{
        console.log(value,data)
        if(value===false){
            setSelectAll(false);
        }
           }
   })
   function handleClick(e){

e.target.checked?setSelectAll(true):setSelectAll(false);
;

   }

  return (<div>

        <label class="container"><input onChange={handleClick} type="checkbox" />Select All
  <span class="checkmark"></span>   <button id="delete"  class="btn btn-danger ">delete</button></label>

      <ul>
        <li>
        { props.list.map((index)=>{

          return <DeleteItem data={index} selectAll={selectAll} selected={selectItems}/>
        })}
       </li>

      </ul>

  </div>)
}
