import React, { Component } from 'react';
import DeleteItem2 from "./DeleteItem2";
import ProductItem from "./ProductItem";
class DeleteList2 extends Component {

    state = {
        checked:true
      }
    handleClick=(e)=>{
        console.log("inside handleclick")
      e.target.checked?this.setState({checked:false}):this.setState({checked:true})
      this.forceUpdate();
      console.log(this.state.checked);  
    }

    render() { 
        return (<div>

            <label class="container"><input onChange={this.handleClick} type="checkbox" checked={this.state.checked} />Select All
      <span class="checkmark"></span>   <button id="delete"  class="btn btn-danger ">delete</button></label>

          <ul>
            <li>
            { this.props.list.map((index)=>{

              return <DeleteItem2 data={index} selectAll={this.state.checked} />
            })}
           </li>

          </ul>

      </div>);
    }
}

export default DeleteList2;
