import React from "react";
import { connect } from 'react-redux';
function ViewDetails(props) {
  console.log("In view details",props.product)
    if(props.product===undefined){
return <div>Please select an item from Home tab to view details or <a href='/about'>Click here to redirect</a></div>

}else{
return <div
><div class="card ">
<div class="card-header">
Product Details
</div>
<div class="card-body">
<h5 class="card-title">Product Name:<h6> {props.product.productName}</h6></h5>
<h5 class="card-title">Model: {props.product.model}</h5>
<h5 class="card-title">Price: ${props.product.price}</h5>
<h5 class="card-title">Quantity: {props.product.quantity}</h5>
<h5 class="card-title">Description: {props.product.productDescription}</h5>
<h5 class="card-title">Manufacturer: {props.product.manufacturer}</h5>
<h5 class="card-title">Author: {props.product.author}</h5>
<h5 class="card-title">Created on: {props.product.created}</h5>
<h5 class="card-title">Last Updated on: {props.product.updated}</h5>

  <h6 class="card-text">With supporting text below as a natural lead-in </h6>
</div>
{/* <div class="card-footer text-muted">
  2 days ago
</div> */}
</div>{props.product.id}</div>
}


}
const mapStateToProps = state =>{
    return {
      products: state.products,
      product:state.product
    }
  }

  export default connect(mapStateToProps)(ViewDetails)
