import React, { Component } from 'react';
import DeleteItem2 from "./DeleteItem2";
import axios from 'axios';
import { connect } from 'react-redux';
import ProductItem from "./ProductItem";
class DeleteList2 extends Component {
    constructor(props){
   this.state = {
        checked: false,
        deleteItems:[],
        count:0,
        show:props.list
      }}
    handleClick=(e)=>{
        console.log("inside handleclick",   e.target.checked)
  e.target.checked?
  this.setState({checked:true,deleteItems:this.props.products,count:this.state.deleteItems.length})
  :this.setState({checked:false, deleteItems:[],count:0});

     console.log(this.state.deleteItems);  
     console.log("Here I am length",this.state.deleteItems.length);
     this.forceUpdate();
    }
    delItems=()=>{
     const res=   axios.delete('http://localhost:3000/products${this.state.id}')
        .then(res => {
          console.log(res);
          console.log(res.data);
        })
    }
  selectItems=(value,data)=>{
    console.log("back in list",value,data)
      if(value) {this.state.deleteItems.push(data)}
      else{
        this.setState({deleteItems:this.state.deleteItems.filter(function(item) {
            return item !== data
        })})

      }  
      this.forceUpdate()

  }
  deleted=(data)=>{
    this.setState({show:this.state.show.filter(function(item) {
        return item !== data
    })})
    console.log("here in deletion")
    this.forceUpdate()
  }
    render() { 
        return (<div>

            <label class="container"><input onChange={this.handleClick} type="checkbox"  />Select All
      <span class="checkmark"></span>{this.state.deleteItems.length}   <button id="delete"  onClick={this.delItems} class="btn btn-danger ">delete</button></label>
           {console.log("show",this.state.show)}
           {console.log("list",this.props.list)}
          <ul>
            <li>
            { this.state.show.map((index)=>{

              return <DeleteItem2 data={index} deleted={this.deleted} selectItems={this.selectItems} selectAll={this.state.checked} />
            })}
           </li>

          </ul>

      </div>);
    }
}
const mapStateToProps = state =>{
    return {
      products: state.products
    }
  }

export default connect(mapStateToProps)(DeleteList2)
