import React from "react";
import { connect } from 'react-redux';
import './ViewDetails.css'
function ViewDetails(props) {
  console.log("In view details",props.product)
    if(props.product===undefined){
return <div>Please select an item from Home tab to view details or <a href='/about'>Click here to redirect</a></div>

}else{
return <div
><div class="card ">
<div class="card-header">
Product Details
</div>
<div class="card-body">
<p class="label">Product Name:<p class="data"> {props.product.productName}</p></p>
<p class="label">Model:<p class="data"> {props.product.model}</p></p>
<p class="label">Price:<p class="data"> ${props.product.price}</p></p>
<p class="label">Quantity: <p class="data"> {props.product.quantity}</p></p>
<p class="label">Description: <p class="data"> {props.product.productDescription}</p></p>
<p class="label">Manufacturer:<p class="data"> {props.product.manufacturer}</p></p>
<p class="label">Author:<p class="data"> {props.product.author}</p></p>
<p class="label">Created on:<p class="data"> {props.product.created}</p></p>
<p class="label">Last Updated on: <p class="data"> {props.product.updated}</p></p>

  <h6 class="card-text">With supporting text below as a natural lead-in </h6>
</div>
{/* <div class="card-footer text-muted">
  2 days ago
</div> */}
</div>{props.product.id}</div>
}


}
const mapStateToProps = state =>{
    return {
      products: state.products,
      product:state.product
    }
  }

  export default connect(mapStateToProps)(ViewDetails)
