import React, { useEffect, useState, lazy, Suspense } from "react";
import {
  useHistory,
  Redirect,
  BrowserRouter as Router,
} from "react-router-dom";
import "./About.css";
import { connect } from "react-redux";
import { CSSTransition } from "react-transition-group";
import "./About2.css";
import { SubItems1 } from "../data/NavbarItems";
const ProductList = lazy(() => import("./ProductList"));
export function About(props) {
  let history = useHistory();
  function handler() {
    history.push("/products/view");
  }

  const [products, setProducts] = useState(props.products);
  const [options, setOptions] = useState(props.products);
  const [productType, setPT] = useState([]);
  const [search, setSearch] = useState("");
  const [touch, touched] = useState(false);
  const [customised,customise]= useState([]);
  const [filter,setFilter]=useState(true)
  const toggle = () => {
    touched(!touch);
  };
  const mainFilter=()=>{
     console.log(customised)
    let prods=[]
    customised.map((item)=>{

     prods= prods.concat(
        props.products.filter((product) => {
        return (
          (product.manufacturer
            .indexOf(item) !== -1 )
        );
      }
      ))})
      console.log("prods",prods)
      setProducts(prods)
  }
  const handleFilter = (e) => {
    console.log(e.target.checked)
    console.log(e.target.value);
   (e.target.checked)?
   customised.push(e.target.value)
  :customise(customised.filter((i)=>{ if(i!==e.target.value){
     return i;
   }}))

 mainFilter()
}

    // let filteredProducts = products.filter((product) => {
    //   return (
    //     (product.manufacturer
    //       .toLowerCase()
    //       .indexOf(e) !== -1 )
    //   );
    // });
    // setProducts(filteredProducts);


  const handleClick = () => {
    let filteredProducts = products.filter((product) => {
      return (
        product.productName
          .toLowerCase()
          .indexOf(search.toLowerCase().trim()) !== -1
      );
    });
    setProducts(filteredProducts);
  };

  const onChange = (event) => {
    setSearch(event.target.value);
    console.log("Change recorded");
    console.log(event.target.value);
    handleClick();
  };
  useEffect(() => {
    if (search === ""&&customised.length===0) {
      console.log("in here");
      setProducts(props.products);
      setOptions(props.options);
      setPT(props.productTypes);
     // console.log(customised)
      // setOptions(Array.from(new Set(props.products.map((item)=>{return item.name}))))
    }
console.log(customised)
  });

  return (
    <div class="ggg">
      <div class="search-container ggg  ">
        <div>
          {" "}
          <input
            class="input-text"
            id="inn"
            onChange={onChange}
            type="text"
            placeholder="Search any item..."
          ></input>
          <button
            class="btn btn-dark text-white"
            style={{ position: "fixed" }}
            onClick={handleClick}
          >
            Search
          </button>
        </div>
      </div>
      <div class="mx-auto" style={{ width: 50 }}>
        <button onClick={toggle}>Features</button>
      </div>

      <CSSTransition
        in={touch}
        timeout={500}
        classNames="balloon"
        unmountOnExit
      >
        <div
          className="mx-auto hhhhh p2 row justify-content-center'"
          style={{ width: 500, height: 150,color:'black' }}
        >
          <ul>
            Brands
            <li>
              {options.map((item) => {
                return (
                  <div>
                    <input type="checkbox" value={item}onClick={handleFilter} />
                    <label>{item}</label>
                  </div>
                );
              })}
            </li>
          </ul>
          <div>
            {" "}
            <ul>
              ProductType
              <li>
                {productType.map((item) => {
                  return (
                    <div>
                      <input type="checkbox" />
                      <label>{item}</label>
                    </div>
                  );
                })}
              </li>
            </ul>
          </div>
          <div>
            <ul>
             Price Range
              <li>
                <input type="checkbox" />
                <label>10000-2000</label>
              </li>
              <li>
                <input type="checkbox" />
                <label>20000-40000</label>
              </li>
              <li>
                <input type="checkbox" />
                <label>40000-60000</label>
              </li>
            </ul>
          </div>
        </div>
      </CSSTransition>
      <Suspense fallback={<div>Loading</div>}>
        <ProductList list={products}></ProductList>
      </Suspense>
    </div>
  );
}
const mapStateToProps = (state) => {
  return {
    products: state.products,
    options: state.brands,
    productTypes: state.productTypes,
  };
};

export default connect(mapStateToProps)(About);
