import React, { useEffect, useState, lazy, Suspense } from "react";
import {
  useHistory,
  Redirect,
  BrowserRouter as Router,
} from "react-router-dom";
import "./About.css";
import { connect } from "react-redux";
import { CSSTransition } from "react-transition-group";
import './About2.css'
import { SubItems1 } from "../data/NavbarItems";
const ProductList = lazy(() => import("./ProductList"));
export function About(props) {
  let history = useHistory();
  function handler() {
    history.push("/products/view");
  }

  const [products, setProducts] = useState(props.products);
  const [options, setOptions] = useState(props.products);
  const [search, setSearch] = useState("");
  const [touch,touched]=useState(false);

  const toggle=()=>{
     touched(!touch)
  }
  const handleClick = () => {
    let filteredProducts = products.filter((product) => {
      return (
        product.productName
          .toLowerCase()
          .indexOf(search.toLowerCase().trim()) !== -1
      );
    });
    setProducts(filteredProducts);
  };

  const onChange = (event) => {
    setSearch(event.target.value);
    console.log("Change recorded");
    console.log(event.target.value);
    handleClick();
  };
  useEffect(() => {
    if (search === "") {
      console.log("in here");
      setProducts(props.products);
      setOptions(Array.from(new Set(props.products.map((item)=>{return item.name}))))
    }

  });

  return (
    <div class="ggg">
      <div class="search-container ggg  ">
        <div>
          {" "}
          <input
            class="input-text"
            id="inn"
            onChange={onChange}
            type="text"
            placeholder="Search any item..."
          ></input>
          <button class="btn btn-dark text-white"style={{position:"fixed"}} onClick={handleClick}>
            Search
          </button>
        </div>
      </div>
  <div class="mx-auto" style={{width:50}}><button onClick={toggle}>Features</button></div>

    <CSSTransition
       in={touch}
      timeout={500}
       classNames='balloon'
       unmountOnExit
     >
     <div className="mx-auto hhhhh p2" style={{width:500,height:100}}>{options}</div>

     </CSSTransition> 
      <Suspense fallback={<div>Loading</div>}>
        <ProductList list={products}></ProductList>
      </Suspense>

    </div>
  );
}
const mapStateToProps = (state) => {
  return {
    products: state.products,
  };
};


export default connect(mapStateToProps)(About);
